
var swiper = new Swiper(".main .swiper", {
    speed: 1000,
    autoplay:
    {
        delay: 2000,
    },
    loop: true,
    pagination: {
        el: ".main .swiper-pagination",
        clickable: true
    },
});

var menu = 0;

$(".openburgerbtn").click(function (event) {
    if (menu === 0) {
        $(".header__right").slideDown(500);
        menu = 1;
    } else {
        $(".header__right").slideUp(500);
        menu = 0
    }
    event.stopPropagation()
    $("body").click(function () {
        $(".header__right").slideUp(500);
        menu = 0
    })
})