var showed = 0;
var height = $(".different_flex").height()
$(".different_flex").css("height", "430px")
$(".showalldifferent").click(function(event){
    if(showed==0){
        $(".different_flex").css("height", ""+height+"px");
        $(this).addClass("showed");
        $(this).text("Свернуть")
        showed=1
    }
    else{
        $(".different_flex").css("height", "430px");
        $(this).removeClass("showed");
        $(this).text("Показать все приемущества")
        showed=0
    }
})
