$(".langtitle").click(function (event) {
    $(".userselect_hide").slideUp(500)
    $(".lang_hide").slideToggle(500)
    event.stopPropagation()
    $("body").click(function(){
        $(".lang_hide").slideUp(500)
    })
})
$(".userselect_title").click(function (event) {
    $(".userselect_hide").slideToggle(500)
    $(".lang_hide").slideUp(500)
    event.stopPropagation()
    $("body").click(function(){
        $(".userselect_hide").slideUp(500)
    })
})
var menu = 0;
$(".openmenubtn").click(function () {
    if (menu == 0) {
        $(".header_right_menu").css("width", "100%")
        menu = 1
    } else {
        $(".header_right_menu").css("width", "0")
        menu = 0
    }
})
$(".chekmailbox").click(function () {
    if ($("#chekmail").is(':checked')) {
        $("#new_user_email").hide()
    } else {
        $("#new_user_email").show()
    }
})
$(".clousmodal").click(function () {
    $(".registr").hide()
})
$(".openregmodal, .openregmodal").click(function (event) {
    $(".registr").hide()
    $(".regmodal").css("display","flex")
    event.stopPropagation();
    $("body").click(function(){
        $(".registr").hide()
    })
})
$(".registr_cont").click(function(event){
    event.stopPropagation();
})
$(".openlginmodal, .openloginmodal").click(function (event) {
    $(".registr").hide()
    $(".loginmodal").css("display","flex")
    event.stopPropagation();
    $("body").click(function(){
        $(".registr").hide()
    })
})
new WOW().init();
$(".link").on('click', function () {
    let name = $(this).attr('data-name')
    $('html, body').animate({
        scrollTop: $(name).position().top
    }, 500);
});
