var menu = 0;
$(".openmenubtn").click(function(){
    if(menu==0){
        $(".menu").css("width", "100%")
        menu=1
    }else{
        $(".menu").css("width", "0")
        menu=0
    }
})
var swiper = new Swiper(".abaut_complex_thumbswiper", {
    loop: true,
    spaceBetween: 26,
    slidesPerView: "auto",
    watchSlidesProgress: true,
    navigation: {
        nextEl: ".abaut_complex .slidenext",
        prevEl: ".abaut_complex .slideprev",
    },
});
var swiper2 = new Swiper(".abaut_complex_galleryswiper", {
    loop: true,
    spaceBetween: 10,
    navigation: {
        nextEl: ".abaut_complex .slidenext",
        prevEl: ".abaut_complex .slideprev",
    },
    thumbs: {
        swiper: swiper,
    },
});

