var selectedloftfotm = $(".loft_page_forms_box_js")
$(".setform").click(function(){
    $(".setform").removeClass("active")
    $(this).addClass("active")
    $(selectedloftfotm).hide()
    $(selectedloftfotm[$(this).val()]).show()
})
$(".link_slide").on('click', function(){
    $('html, body').animate({
        scrollTop:$(".complexes").position().top
    }, 500);
});
