let loader = document.getElementById("loader")
window.onload = function () {
    setTimeout(function () {
        loader.children[0].style.opacity = 0
    }, 500)
    setTimeout(function () {
        loader.children[1].style.width = 0
        loader.children[2].style.width = 0
    }, 1000)
    setTimeout(function () {
        loader.style.display = "none"
    }, 2000)
};