var mount = ["January","February","March","April","May","June","July","August","September","October","November","December"]
var week = ["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday","Sunday"]
$(document).on('change', '#datepicker', function() { 
    var selectedmount = 0;
    var selecteweek = 0;
    var date = $(this).datepicker('getDate');
    var month = date.getMonth();
    var year = date.getFullYear();
    var day= date.getDate();
    var dayOfWeek = date.getUTCDay();
    if(month==01){selectedmount = mount[0]}
    if(month==02){selectedmount = mount[1]}
    if(month==03){selectedmount = mount[2]}
    if(month==04){selectedmount = mount[3]}
    if(month==05){selectedmount = mount[4]}
    if(month==06){selectedmount = mount[5]}
    if(month==07){selectedmount = mount[6]}
    if(month==08){selectedmount = mount[7]}
    if(month==09){selectedmount = mount[8]}
    if(month==10){selectedmount = mount[9]}
    if(month==11){selectedmount = mount[10]}
    if(month==12){selectedmount = mount[11]}
    if(dayOfWeek==0){selecteweekt = week[0]}
    if(dayOfWeek==1){selecteweekt = week[1]}
    if(dayOfWeek==2){selecteweekt = week[2]}
    if(dayOfWeek==3){selecteweekt = week[3]}
    if(dayOfWeek==4){selecteweekt = week[4]}
    if(dayOfWeek==5){selecteweekt = week[5]}
    if(dayOfWeek==6){selecteweekt = week[6]}
    $(".setdays_calendar_right span").text(day)
    $(".setdays_calendar_right p").html(""+selectedmount+" "+year+" <span>"+selecteweekt+"</span>")
});

