$(".checkbox input").click(function(){
    $(".checkbox input").prop("checked", false);
    $(this).prop("checked", true);
})
var menu = 0;
$(".openmenubtn").click(function(){
    $(".openmenubtn").toggleClass("openmenubtnactive")
    if(menu==0){
        $(".header_cont_right").css("width", "100%");
        menu = 1
    }else{
        $(".header_cont_right").css("width", "0");
        menu = 0
    }
})
var resizemenu = 0
$(window).resize(function(){
    if($(window).width()>1170 && menu == 0){
        $(".header_cont_right").css("width", "100%");
        menu = 1;
    }
    if($(window).width()<1170 && menu == 1){
        $(".header_cont_right").css("width", "0");
        menu = 0;
    }
})