var menu = 0;
$(".burgermenu").click(function(){
    if(menu==0){
        $(".header_cont ul").css("width", "100%");
        menu = 1
    }else{
        $(".header_cont ul").css("width", "0");
        menu = 0
    }
})
$(".scrolllink").click(function(){
    $('html, body').animate({
        scrollTop:$("."+$(this).data("src")+"").position().top
    }, 500);
});