$(window).ready(function(){
    $("html").css("visibility", "unset")
    $(".clous").click(function(){
        $(".download-app").slideUp(500)
    })
    
    let showHeight = $(".show-hide-text").height()
    let setHeightStage = 0;
    $(".show-hide-text").css("height", "70px")
    
    $(".show-hide-btn").click(function(){
        if(setHeightStage===0) {
            $(".show-hide-text").css("height", ""+showHeight+"px")
            $(this).text("меньше")
            setHeightStage=1
        }else{
            $(".show-hide-text").css("height", "70px")
            $(this).text("...еще")
            setHeightStage=0
        }
    })
})