var menu = 0;
$(".openmenubtn").click(function(){
  if(menu==0){
    $(".header_bottom").css("width","100%")
    menu=1
  }else{
    $(".header_bottom").css("width","0")
    menu=0
  }
})
$(".header_bottom_cont_mob form").click(function(event){
  event.stopPropagation();
});
$(".aopensearcc").click(function(event){
  $(".header_bottom_cont_mob").addClass("activsearchmob");
  event.stopPropagation();
  $("body").click(function(){
    $(".header_bottom_cont_mob").removeClass("activsearchmob");
  })
})
var b = 1;
$(".countminus").click(function(){
  if(b>1){
    b-=1
    $(this).siblings("span").text(b)
  }
})
$(".countplus").click(function(){
  b+=1;
  $(this).siblings("span").text(b)
})
$(".catalog_page_cont_left_menu_box_item_title").click(function(){
  $(this).siblings(".catalog_page_cont_left_menu_box_item_js").slideToggle(500)
})
var filter = 0;
$(".openfilter").click(function(){
  if(filter==0){
    $(".catalog_page_cont_left").css("width","100%")
    filter=1;
  }else{
    $(".catalog_page_cont_left").css("width","0")
    filter=0;
  }
})
var opisjs = $(".productsingl_cont_bottom_js")
$(opisjs).hide()
$(opisjs[0]).show()
$(".productopisbtn").click(function(){
  $(".productopisbtn").removeClass("active")
  $(this).addClass("active")
  $(opisjs).hide()
  $(opisjs[$(this).val()]).show()
})
$(".opencategory").click(function(){
  $(".searchcategory").slideToggle(500)
})
$(".callbackabsalute, .openmodalss").click(function(event){
  $(".callbackmodal").show()
  event.stopPropagation()
  $("body").click(function(){
    $(".callbackmodal").hide()
  })
})
$(".callnumber").click(function(event){
  $(".number_modal").show()
  event.stopPropagation()
  $("body").click(function(){
    $(".number_modal").hide()
  })
})
$(".callbackmodal_cont,.number_modal_cont").click(function(event){
  event.stopPropagation()
})
$(".clousmodalbtn").click(function(){
  $(".number_modal").hide()
  $(".callbackmodal").hide()
})
$(".showlist").click(function(){
  $(".catalog_page_cont_rigth_grid").addClass("catalog_page_cont_rigth_grid_list")
})
$(".showbox").click(function(){
  $(".catalog_page_cont_rigth_grid").removeClass("catalog_page_cont_rigth_grid_list")
})
$(window).resize(function(){
  if($(window).width()<620){
    $(".catalog_page_cont_rigth_grid").removeClass("catalog_page_cont_rigth_grid_list")
  }
})
var myroom = $(".myroom_cont_right_js")
$(myroom).hide()
$(myroom[0]).show()
$(".myroombtn").click(function(){
  $(myroom).hide()
  $(myroom[$(this).val()]).show()
  $(".myroombtn").removeClass("active")
  $(this).addClass("active")
})
