$("#searchinput").on("input", function () {
    if ($(this).val() == "") {
        $(".clear_input").removeClass("clear_input-active")
        $(".searchform").addClass("searchform_active")
    } else {
        $(".clear_input").addClass("clear_input-active")
        $(".searchform").addClass("searchform_active")
    }
})
$("#searchinput").on("focus", function (event) {
    if ($("#searchinput").val() == "") {
        $(".clear_input").removeClass("clear_input-active")
    } else {
        $(".clear_input").addClass("clear_input-active")
        $(".searchform").addClass("searchform_active")
    }
    event.stopPropagation()
})
$("body").click(function () {
    $(".clear_input").removeClass("clear_input-active")
    $(".searchform").removeClass("searchform_active")
})
$(".searchformbtn").click(function (event) {
    $("#searchinput").val($(this).val())
    $(".clear_input").addClass("clear_input-active")
    $(".searchform").addClass("searchform_active")
    event.stopPropagation()
})
$(".clear_input").click(function (event) {
    $("#searchinput").val("")
    $(".clear_input").removeClass("clear_input-active")
    event.stopPropagation()
})
var cataloghome = $(".homecatalog_right_grid")
$(".setcatalogbtn").click(function () {
    $(cataloghome).hide()
    $(cataloghome[$(this).val()]).css("display", "grid")
    $(".setcatalogbtn").removeClass("active")
    $(this).addClass("active")
})
var swipermain = new Swiper(".main_swiper", {
    pagination: {
        el: ".main_pag",
    },
    loop: true,
});
swipermain.on('slideChange', function () {
    $(".swiper-slide h2").removeClass("animate__ animate__fadeInDown  wow animated")
    $(".swiper-slide p").removeClass("animate__ animate__fadeInUp  wow animated")
    $(".swiper-slide-active h2").addClass("animate__ animate__fadeInDown  wow animated")
    $(".swiper-slide-active p").addClass("animate__ animate__fadeInUp  wow animated")
});
var swiper = new Swiper(".newproducts .swiper ", {
    navigation: {
        nextEl: ".newproducts .slidenext",
        prevEl: ".newproducts .slideprev",
    },
    slidesPerView: "auto",
    loop: true,
});
