var count = 0;
$(".countplus").click(function () {
    count = $(this).siblings("span").text();
    count++;
    $(this).siblings("span").text(count);
})
$(".countminus").click(function () {
    count = $(this).siblings("span").text();
    if (count > 0) {
        count--;
        $(this).siblings("span").text(count);
    }
})
var infoblock = $(".info_text");
$(".setinfo").click(function () {
    $(".setinfo").removeClass("active");
    $(this).addClass("active");
    $(infoblock).hide();
    $(infoblock[$(this).val()]).show();
})
$(".openmenubtn").click(function(){
    $(".header_bottom").slideToggle(500)
})
new WOW().init();