$(".openmenubtn").click(function () {
    $(this).toggleClass("activebtn")
    $(".menu").slideToggle(500)
})

$(".link").on('click', function () {
    let name = $(this).attr('data-src')
    if($(window).width()<769) {
        $(".openmenubtn").removeClass("activebtn")
        $(".menu").slideUp(500)
    }
    $('html, body').animate({
        scrollTop: $(name).position().top - 100
    }, 500);
});

var collors_array = [
    "#2E2E2E","#756F6F","#FFF",
    "#600A4D","#FFE5E5","#600A4D",
    "#264D55","#7C9DA5","#FFF",
    "#564C95","#FFFFFF","#564C95"
]
var set_colors = 0;
$(".setcolorbtn").click(function(){
    set_colors +=3
    $(".setcolor1").css("background-color", collors_array[set_colors])
    $(".setcolor2").css("background-color", collors_array[set_colors+1])
    $(".setcolor3").css("color", collors_array[set_colors+2])
    $(".setcolor4 path").css("fill", collors_array[set_colors+2])
    if(set_colors >= collors_array.length) {
        set_colors = 0
        $(".setcolor1").css("background-color", collors_array[set_colors])
        $(".setcolor2").css("background-color", collors_array[set_colors+1])
        $(".setcolor3").css("color", collors_array[set_colors+2])
        $(".setcolor4 path").css("fill", collors_array[set_colors+2])
    }
})

var porfolioItem = $(".portfolio .portfolio_item");
var sek = 0;
let b = 0;
for(var i = 0; i < porfolioItem.length; i++) {
    b+=1;
    sek+=0.5
    $(porfolioItem[i]).css("animation-duration", sek+"s")
    if(b==9) {
        sek=0;
        b=0
    }
}
$(".stakes_item").on({
    mouseenter: function () {
        $(this).children(".stage").css("opacity", "1")
        let stage = $(this).children(".stage").attr("data-stage")
        for(let stagMax = 0; stagMax <= stage; stagMax++) {
            $(this).children(".stage").children("span").css("left", ""+(stagMax-16)+"%")
            $(this).children(".stage").children("span").text(""+stagMax+" %")
            $(this).children(".stage").children(".stageline").children(".stagelineconfirm").css("width", ""+ (stagMax+1) +"%")   
        }
    },
    mouseleave: function () {
        $(".stage").css("opacity", "0")
        $(".stage span").css("left", "-16%")
        $(".stage span").text("0 %")
        $(".stage .stagelineconfirm").css("width", "0")
    }
});