var portfolio = $(".portfolio__cont_js")
$(".set-btn").click(function () {
    $(".set-btn").removeClass("active");
    $(this).addClass("active")
    $(portfolio).hide()
    $(portfolio[$(this).val()]).show();
})
var menu = 0;
$(".openmenubtn").click(function () {
    if (menu == 0) {
        $(".menu").css("width", "100%");
        menu = 1
    }
    else {
        $(".menu").css("width", "0");
        menu = 0
    }
})
new WOW().init();
$(".lang").click(function () {
    $(".lang .hidden").slideToggle(500);
    $(".setlang").click(function () {
        $(".activelang img").attr("src", $(this).children("img").attr("src"))
    })
})
$(".open_job").click(function () {
    $(this).parent().hide()
    $(this).parent().siblings(".job_item_hidden").slideDown(500)
})
$(".hidejob").click(function () {
    $(this).parent().parent().hide()
    $(this).parent().parent().siblings(".job_item_show").slideDown(500)
})
$(".link").on('click', function () {
    let name = $(this).attr('data-src')
    $('html, body').animate({
        scrollTop: $(name).position().top
    }, 500);
});
var animsvg = $(".animate-svg");
var i = 0;
var b = 0
var e = 0
var deg = 0
console.log(animsvg.length)
setInterval(function(){
    if(b<100){
        $(".training_img").css("transform","rotate("+deg+"deg)")
        $(animsvg[i]).css("opacity",$(animsvg[i]).data("opacity"))
        i+=1
        b++
        deg+=0.8
    }
    if(b>=100 && e<100){
        $(".training_img").css("transform","rotate("+deg+"deg)")
        $(animsvg[i]).css("opacity", "0")
        deg+=0.8
        i-=1
        e++
    }
    if(b>=100 && e>=100){
        b=0
        e=0
        i=0
    }
},20)