
var swiper = new Swiper(".main_swiper", {
    loop: true,
    pagination: {
        el: ".mainpag",
    },
});
var swiper = new Swiper(".homework__swiper", {
    loop: true,
    navigation: {
        nextEl: ".slidenext",
        prevEl: ".slideprev",
    },
    slidesPerView: "auto",
    spaceBetween: 20,
    freeMode: false,
});
var homcat = $(".homeproducts_grid")
$(".setcategory").click(function(){
    $(".setcategory").removeClass("active")
    $(this).addClass("active")
    $(homcat).hide()
    $(homcat[$(this).val()]).css("display","grid")
})
$(".showcat").click(function(){
    $(this).siblings(".catalog_item_hide").slideToggle(500)
    $(this).children("span:nth-child(2)").toggle()
})
var count = 0;
$(".countplus").click(function(){
    count = $(this).siblings("span").text();
    count++;
    $(this).siblings("span").text(count)
})
$(".countminus").click(function(){
    if($(this).siblings("span").text()>1){
        count = $(this).siblings("span").text();
        count--;
        $(this).siblings("span").text(count)
    }
})
var infoblok = $(".infoblock_Js")
$(".setinfobtn").click(function(){
    $(".setinfobtn").removeClass("active")
    $(this).addClass("active")
    $(infoblok).hide();
    $(infoblok[$(this).val()]).show()
})
$(".callbackabsalute, .callback button").click(function(){
    $(".callbackmodal").show()
})
$(".callnumber").click(function(){
    $(".number_modal").show()
})
$(".clousmodalbtn").click(function(){
    $(".number_modal").hide()
    $(".callbackmodal").hide()
})
var menu = 0;
$(".openmenu").click(function(){
    if(menu==0){
        $(".header_bottom").css("width","100%");
        menu=1
    }else{
        $(".header_bottom").css("width","0");
        menu=0
    }
})